        /*http://thewikimachine.fbk.eu/gui/basic
        Host: thewikimachine.fbk.eu
User-Agent: Mozilla/5.0 (X11; U; Linux x86_64; en-US; rv:1.9.2.12) Gecko/20101027 Ubuntu/10.10 (maverick) Firefox/3.6.12
Accept: text/html,application/xhtml+xml,application/xml;q=0.9,;q=0.8
Accept-Language: en-us,en;q=0.5
Accept-Encoding: gzip,deflate
Accept-Charset: ISO-8859-1,utf-8;q=0.7,*;q=0.7
Keep-Alive: 115
Connection: keep-alive
Referer: http://thewikimachine.fbk.eu/gui/
         */

        /**
         * @param textWithHTMLAnnotation
         * @return
         */
        public static String parseToMatrix(String textWithHTMLAnnotation) {
            StringBuffer buffer = new StringBuffer();
            Matcher matcher = pattern.matcher(textWithHTMLAnnotation);
            int start = 0;
            // Check all occurences
            while (matcher.find()) {
                String text = textWithHTMLAnnotation.substring(start, matcher.start());
                WikiLinkParser.appendToMatrix(text, new DBpediaResource(WikiLinkParser.NoTag), buffer);
                String uri = matcher.group(1);
                String surfaceForm = matcher.group(2);
                WikiLinkParser.appendToMatrix(surfaceForm, new DBpediaResource(uri), buffer);
                start = matcher.end();
            }
            String text = textWithHTMLAnnotation.substring(start);
            WikiLinkParser.appendToMatrix(text, new DBpediaResource(WikiLinkParser.NoTag), buffer);
            return buffer.toString();
        }

